mapper.select:
select cntenr.LinkElement, cntenr.LinkDescription as NotesContent, cntenr.duration from corcontentenrichment cntenr JOIN corenrichment enr ON cntenr.enrichmentid= enr.enrichmentId JOIN sbnsubscriptioncourse sbncourse ON sbncourse.CurriculumCourseId=enr.CurriculumCourseId JOIN sbnmembersubscriptionprogram subprgm ON sbncourse.MemSubscriptionProgId=subprgm.id where cntenr.LinkType=#{contentType}
AND enr.CourseId=#{subscrCourseId}
and enr.LearningComponentId=#{learnCompId} and enr.LearningContentId=#{learnCmpContId} and subprgm.MemberRoleId=#{memberRoleId}

select cntenr.* from corcontentenrichment cntenr JOIN corenrichment enr ON cntenr.enrichmentid= enr.enrichmentId
where
enr.CourseId=#{subscrCourseId}
and enr.LearningComponentId=#{learnCompId} and enr.LearningContentId=#{learnCmpContId} and enr.CreatorMemberRoleId=#{memberRoleId}

select cntenr.LinkElement, cntenr.LinkDescription as NotesContent, cntenr.duration from corcontentenrichment cntenr JOIN corenrichment enr ON cntenr.enrichmentid= enr.enrichmentId JOIN sbnsubscriptioncourse sbncourse ON sbncourse.CurriculumCourseId=enr.CurriculumCourseId JOIN sbnmembersubscriptionprogram subprgm ON sbncourse.MemSubscriptionProgId=subprgm.id where cntenr.LinkType=#{contentType}
AND enr.CourseId=#{subscrCourseId}
and enr.LearningComponentId=#{learnCompId} and enr.LearningContentId=#{learnCmpContId} and subprgm.MemberRoleId=#{memberRoleId}

select cntenr.LinkElement, cntenr.uri, cntenr.duration from corcontentenrichment cntenr JOIN corenrichment enr ON cntenr.enrichmentid= enr.enrichmentId JOIN sbnsubscriptioncourse sbncourse ON sbncourse.CurriculumCourseId=enr.CurriculumCourseId JOIN sbnmembersubscriptionprogram subprgm ON sbncourse.MemSubscriptionProgId=subprgm.id where cntenr.LinkType = #{contentType}
AND enr.CourseId=#{subscrCourseId}
and enr.LearningComponentId=#{learnCompId} and enr.LearningContentId=#{learnCmpContId} and subprgm.MemberRoleId=#{memberRoleId}

select cntenr.LinkElement, cntenr.duration, cntenr.ID, cntenr.parentContent
from corcontentenrichment cntenr JOIN corenrichment enr ON cntenr.enrichmentid= enr.enrichmentId JOIN sbnsubscriptioncourse sbncourse ON sbncourse.CurriculumCourseId=enr.CurriculumCourseId JOIN sbnmembersubscriptionprogram subprgm ON sbncourse.MemSubscriptionProgId=subprgm.id where cntenr.LinkType = 7 AND enr.CourseId=#{subscrCourseId}
and enr.LearningComponentId=#{learnCompId} and enr.LearningContentId=#{learnCmpContId} and subprgm.MemberRoleId=#{memberRoleId}
